var twoSum = function(nums, target) {
    for(let i=0; i<nums.length;i++){
        needed=target-nums[i];
        if(nums.includes(needed) && nums.indexOf(needed)!=i ){
            return [i,nums.indexOf(needed)]
        }
    }
    return []
};

