import React from "react";
import { useState } from "react";

const UseState = () => {
  return (
    <div>
      <p>
        <b>useState</b> is a React Hook that lets you add a state variable to
        your component.
      </p>
      Call useState at the top level of your component to declare a state
      variable.
      <dl>
        <dt>Syntax:</dt>
        <dd>const [state, setState] = useState(initialState);</dd>
      </dl>
    </div>
  );
};

export default UseState;
